# PASTE MAIN FUNCTION HERE
def main():
  h = 0.01
  x = 1.5
  f = "x**7+3*x**2"
  forward = forwardDiff(f,x,h)
  backward = backwardsDiff(f,x,h)
  central = centralDiff(f,x,h)
  print("Approximating derivative of f =",f,"at the value of x =",x,"using a step size of h =", h)
  print("Forward: ", round(forward,5))
  print("Backward: ", round(backward,5))
  print("Central: ", round(central,5))
main()


